import React from "react";
import { useState, useEffect } from "react";

import Postbox from "./Postbox";
import CommentReplyBox from "./CommentReplyBox";

export default function Post(props) {
  const [showReplyPostbox, setShowReplyPostbox] = useState(false);
  const [buttonName, setButtonName] = useState("reply");

  function handleReplyClick(event) {
    event.preventDefault();
    if (buttonName === "reply") {
      setShowReplyPostbox(true);
      setButtonName("close");
    } else if (buttonName === "close") {
      setShowReplyPostbox(false);
      setButtonName("reply");
    }

    //call the route
  }

  //   function handleCommentClick(event) {
  //     event.preventDefault();
  //     if (buttonName === "reply") {
  //       setShowReplyPostbox(true);
  //       setButtonName("close");
  //     } else if (buttonName === "close") {
  //       setShowReplyPostbox(false);
  //       setButtonName("reply");
  //     }
  //   }

  return (
    <div
      //   onClick={handleCommentClick}
      style={{
        borderStyle: "solid",
        borderColor: "black",
        marginTop: "5px",
        marginBottom: "5px",
        marginLeft: "5px",
        marginRight: "5px",
      }}
    >
      <h3>{props.username} said:</h3>
      <p>{props.content}</p>
      <button style={{ marginBottom: "5px" }} onClick={handleReplyClick}>
        {buttonName}
      </button>
      {/* !!!!!!!!!!!!PERHAPS NEED TO REPLACE POSTBOX BY COMMENTBOX */}
      {showReplyPostbox ? <CommentReplyBox id={props.id} /> : null}
    </div>
  );
}
